  #{uname,jdbcType=VARCHAR}, #{usex,jdbcType=VARCHAR}, #{uidnumber,jdbcType=VARCHAR}, 
  #{uphone,jdbcType=INTEGER}, #{uimage,jdbcType=VARCHAR}, #{uemail,jdbcType=VARCHAR}
  )

mapper.select:

select uid, unickname, upassword, uname, usex, uidNumber, uphone, uimage, uemail
from user
where uid = #{uid,jdbcType=VARCHAR}
